const SET_AUTH_FIELD = 'SET_AUTH_FIELD';
const initialState = {
  first_name: '',
  last_name: '',
  email: '',
  phone_number: '',
  password: '',
  company_name: '',
  company_type: '',
  title: '',
  address: '',
  company_website: '',
  building_types: '',
  project_types: '',
  number_of_hourly_employees_range: '0-5'
}; // actions

export const setAuthField = (e, key) => ({
  type: SET_AUTH_FIELD,
  val: e,
  key
});
export const authState = state => ({
  auth: state.auth
});
export const authDispatcher = dispatch => ({
  setAuthField: (e, key) => dispatch(setAuthField(e, key))
});
export default function authReducer(state = initialState, action) {
  if (action.type === SET_AUTH_FIELD) {
    return { ...state,
      [action.key]: action.val
    };
  }

  return state;
}
